mapper.select:
SELECT
detection_record_id,
user_id,
classify,
diagnosis,
create_time
FROM detection_record_summary

    AND user_id = #{userId}

        AND classify = #{dto.classify}



        AND diagnosis LIKE CONCAT('%', #{dto.diagnosis}, '%')


        AND create_time >= #{dto.startTime}


        AND create_time <= #{dto.endTime}


ORDER BY create_time DESC

SELECT classify, COUNT(*) AS count FROM detection_record_summary
AND create_time >= #{startTime}
AND create_time <= #{endTime}
GROUP BY classify

SELECT
COUNT(*) AS count
FROM detection_record_summary


        AND create_time >= #{startTime}


        AND create_time <= #{endTime}
